import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Contacts {
  firstName: string;
  lastName: string;
  phoneNumber: string;
}

@Component({
  selector: 'app-create-modal',
  templateUrl: './create-modal.component.html'
})
export class CreateModalComponent {
  contact: Contacts = {
    firstName: '',
    lastName: '',
    phoneNumber: ''
  };

  constructor(private http: HttpClient) { }

  saveContact() {
    // Make the API call here
    this.http.post('/api/contacts', this.contact)
      .subscribe(
        () => {
          console.log('Contact created successfully!');
          // Additional logic or navigation can be added here
        },
        (error) => {
          console.error('Error creating contact:', error);
          // Handle the error appropriately
        }
      );
  }
}
<form (ngSubmit)="saveContact()" class="create-modal-form">
  <div class="form-group">
    <label for="firstName">First Name:</label>
    <input type="text" id="firstName" [(ngModel)]="contact.firstName" name="firstName" required>
  </div>
  <div class="form-group">
    <label for="lastName">Last Name:</label>
    <input type="text" id="lastName" [(ngModel)]="contact.lastName" name="lastName" required>
  </div>
  <div class="form-group">
    <label for="phoneNumber">Phone Number:</label>
    <input type="text" id="phoneNumber" [(ngModel)]="contact.phoneNumber" name="phoneNumber" required>
  </div>
  <button type="submit" class="btn btn-primary">Save Changes</button>
</form>
